package javabasics;

public class LargestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =20;
		int b = 30;
		
		if(a>b)
		{
			System.out.println("a is larger");
		}
		else if(a==b)
		{
			System.out.println("a and b are equal");
		}
		else
		{
			System.out.println("b is larger");
		}

	}

}
